    date_of_birth DATE
);

CREATE TABLE Treatments (
    treatment_id INT PRIMARY KEY,
    treatment_name STRING,
    cost DECIMAL,
    effectiveness STRING
);

CREATE TABLE Doctors (
    doctor_id INT PRIMARY KEY,
    doctor_name STRING,
    specialization STRING,
    years_of_experience INT
);

