    facility_id UUID,
    date DATE,
    waste_type TEXT,
    quantity FLOAT
);

CREATE TABLE collaboration (
    collaboration_id UUID PRIMARY KEY,
    agency_id UUID,
    facility_id UUID,
    collaboration_type TEXT
);

### STATEMENT

SELECT * FROM collaboration WHERE agency_id IN (SELECT agency_id FROM government_agencies WHERE agency_name = 'Environmental Protection Agency');
