CREATE TABLE smart_home_devices (
  device_id UUID PRIMARY KEY,
  device_name TEXT,
  device_type TEXT,
  firmware_version DECIMAL
);

CREATE TABLE device_updates (
  update_id UUID PRIMARY KEY,
  device_id UUID,
  update_type TEXT,
  update_status TEXT,
  update_timestamp TIMESTAMP
);

CREATE TABLE user_devices (
  user_id UUID,
  device_id UUID,
  device_name TEXT,
  is_primary BOOLEAN,
  PRIMARY KEY (user_id, device_id)
);

CREATE TABLE upgrade_logs (
  log_id UUID PRIMARY KEY,
  update_id UUID,
  log_message TEXT,
  log_timestamp TIMESTAMP
);

CREATE TABLE firmware_versions (
  version_id UUID PRIMARY KEY,
  version_number DECIMAL,
  release_date DATE
);

