    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_roles (
    id INT,
    role_id INT,
    PRIMARY KEY (id, role_id),
    FOREIGN KEY (id) REFERENCES users(id),
    FOREIGN KEY (role_id) REFERENCES roles(role_id)
);

CREATE TABLE permissions (
    permission_id INT PRIMARY KEY,
    permission_name VARCHAR(50) NOT NULL
);

### STATEMENT

SELECT u.username AS user_name, r.role_name AS role, p.permission_name AS permission FROM users u JOIN user_roles ur ON u.id = ur.id JOIN roles r ON ur.role_id = r.role_id JOIN permissions p ON r.role_id = p.permission_id;

